function downloadBlob(blob, filename) {
  // Create an object URL for the blob object
  const url = URL.createObjectURL(blob);

  // Create a new anchor element
  const a = document.createElement('a');

  // Set the href and download attributes for the anchor element
  // You can optionally set other attributes like `title`, etc
  // Especially, if the anchor element will be attached to the DOM
  a.href = url;
  a.download = filename || 'download';
  a.textContent = "EXPORT RECORDS"

  // Return the anchor element
  // Useful if you want a reference to the element
  // in order to attach it to the DOM or use it in some other way
  return a;
}

//Testing for download
// Blob object for the content to be download
const blob = new Blob(
  [ 'Vehicle,0367,Dex,IC15\n0.55155,19.258,3.4012,1.7465\n0.73538,1.4708,1.2869,2.2062\n2.436,0.36768,2.8496,1.5627' ],
  { type: 'text/csv' }
);


// Create a download link for the blob content
const downloadLink = downloadBlob(blob, 'records.csv');

// Set the title and classnames of the link
downloadLink.title = 'Export Records as CSV';
downloadLink.classList.add('btn-link', 'download-link');

// Set the text content of the download link
//downloadLink.textContent = 'Export Records';

// Attach the link to the DOM
document.body.appendChild(downloadLink);


const TestDownload = (props) =>{
  const url = URL.createObjectURL(props.object);

  //a.href = url;
  //a.download = filename || 'download';
  //a.textContent = "EXPORT RECORDS"

  return (
    <div className="csv parent">
      <a className="downloadButton" href={url} download= {props.filename} >{props.message} </a>
    </div>

  );
};

