import React from 'react';
import { styled } from '@material-ui/core/styles';
import { withStyles, makeStyles } from '@material-ui/core/styles';

import ToggleButton from '@material-ui/lab/ToggleButton';


//learned something today: the text in a button is not encoded in a property value like background
// need to do <Button> text <Button> to see your text


//Note: withstyles basically overrides everything,
//styled applies your settings, then applies material-ui settings. so some settings may be overided.

