import React from 'react';
import { styled } from '@material-ui/core/styles';
import { withStyles, makeStyles } from '@material-ui/core/styles';

import ToggleButton from '@material-ui/lab/ToggleButton';


//learned something today: the text in a button is not encoded in a property value like background
// need to do <Button> text <Button> to see your text

/*
const HelperButton = styled(ToggleButton)({
  background: 'linear-gradient(45deg, #56CCF2 30%, #2F80ED 90%)',
  //boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  border: 0,
  borderRadius: 5,
  color: 'white',
  height: 48,
  padding: '0 30px',
  display: 'inline-block'
});
*/
//export default StyleToggleButton;

const HelperButton = withStyles((theme) => (
  {
  background: 'linear-gradient(45deg, #56CCF2 30%, #2F80ED 90%)',
  //boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
  border: 0,
  borderRadius: 5,
  color: 'white',
  height: 48,
  padding: '0 30px',
  display: 'inline-block',
  label: 'hello'
}))(ToggleButton);



export default function GradeintButton(props) {
  return (
      <HelperButton>
      {props.text}
      </HelperButton>
  );
}
