import React from 'react';
import styled from "styled-components";


const HelperSpinner = (props) =>{
  return (
    <div class="ui segment">
      <div style = {{width:960, height:720}}class="ui active dimmer">
        <div class="ui indeterminate text loader">{props.message}</div>
      </div>
    </div>
  );
};

HelperSpinner.defaultProps = {
  message :'Loading...'
};

export const Spinner = styled(HelperSpinner)({
    backgroundColorcolor: 'red!important',
    width:'700px', //700
    borderRadius:5
});





